package day06

def findStartOfMarker(input: String, windowSize: Int): Int = {
  input
    .sliding(windowSize)
    .zipWithIndex
    .find((window, index) => window.toSet.size == windowSize)
    .get._2 + windowSize
}

@main def day6: Unit = {
  val input = ""

  println(s"Part 1 = ${findStartOfMarker(input, 4)}")
  println(s"Part 2 = ${findStartOfMarker(input, 14)}")
}
